INSERT INTO plant (name, description, is_Invasive, is_Native, color, image_FruitURL, image_LeafURL, image_Fruit_Source, image_Leaf_Source, wiki_Link, plantID) VALUES ('Red Raspberry', 'Rubus idaeus, commonly known as raspberries, are perennial plants with biennial stems that grow from a perennial root system. These plants produce edible red aggregate fruits consisting of numerous drupelets around a central core, with distinct growth patterns in their first and second years.', false, true, 'Red', 'static/plantImages/RaspberriesF.jpg', 'static/plantImages/RaspberriesL', 'By Ivar Leidus - Own work, CC BY-SA 4.0, https://commons.wikimedia.org/w/index.php?curid=96641162', 'By Tomwsulcer - Own work, CC0, https://commons.wikimedia.org/w/index.php?curid=19087683', 'https://en.wikipedia.org/wiki/Rubus_idaeus', -2);
INSERT INTO plant (name, description, is_Invasive, is_Native, color, image_FruitURL, image_Fruit_Source, wiki_Link, plantID) VALUES ('New Jersey Tea', 'Ceanothus Americanus, a shrub reaching 18-42 inches (0.5-1m) in height, with thin branches and extensive root systems adapted to wildfires. It features clumps of white flowers on long peduncles and produces dry, dehiscent seed capsules.', false, true, 'White', 'static/plantImages/Ceanothus-americanus.jpg', 'By unknown author, USFWS - United States Fish and Wildlife Service, Sherburne National Wildlife Refuge: Ceanothus americanus L., Public Domain, https://commons.wikimedia.org/w/index.php?curid=666534', 'https://en.wikipedia.org/w/index.php?title=Ceanothus_americanus&oldid=1157315821', -3); 
INSERT INTO plant (name, description, is_Invasive, is_Native, color, image_FruitURL, image_Fruit_Source, wiki_Link, plantID) VALUES ('Gray dogwood', 'Cornus Racemosa, a shrub reaching 1.2-3 m (4-10 ft) in height, forming thickets through underground rhizomes. It bears small white flowers in rounded clusters, followed by white fruits, attracting various bird species. The plants gray bark, oppositely arranged leaves, and reddish or purplish fall foliage contribute to its appearance.', false, true, 'White', 'static\plantImages\Grey-Dogwood(F).jpg', 'By Mitternacht90 at English Wikipedia - Mitternacht90 (talk) (Uploads), Public Domain, https://commons.wikimedia.org/w/index.php?curid=19873364','https://en.wikipedia.org/wiki/Cornus_racemosa', -4);
INSERT INTO plant (name, description, is_Invasive, is_Native, color, image_FruitURL, image_LeafURL, image_Fruit_Source, image_Leaf_Source, wiki_Link, plantID) VALUES ('American hazelnut', 'Corylus americana, is a medium to large shrub, growing 2.5-5 m (8-16 ft) tall with a spreading crown. It blooms in early to mid-spring, featuring hanging male catkins and clusters of tiny female flowers. It produces edible nuts enclosed in leaf-like bracts that mature from July to October.', false, true, 'Brown', 'static/plantImages/American-Hazelnut(F).jpg', 'static\plantImages\American-Hazelnut(L).jpg', 'Public Domain, https://commons.wikimedia.org/w/index.php?curid=655914', 'Public Domain, https://commons.wikimedia.org/w/index.php?curid=655927','https://en.wikipedia.org/wiki/Corylus_americana', -5 );
INSERT INTO plant (name, description, is_Invasive, is_Native, color, image_FruitURL, image_LeafURL, image_Fruit_Source, image_Leaf_Source, wiki_Link, plantID) VALUES ('Fragrant sumac', 'Rhus aromatica, a deciduous shrub in the Anacardiaceae family, is native to North America, thriving in a range of conditions from southern Canada to the lower 48 states. With a rounded form, it grows 2-5 ft tall and wide, producing small yellow flowers in clusters before its leaves emerge. The plants three-leaflet leaves, orange-red fall foliage, and lemon-scented crushed leaves make it a distinctive species.', false, true, 'Yellow', 'static/plantImages/Fragrant-Sumac(F).jpg','static\plantImages\Fragrant-Sumac(L).jpg', 'By Salicyna - Own work, CC BY-SA 4.0, https://commons.wikimedia.org/w/index.php?curid=51291378', 'By Krzysztof Ziarnek, Kenraiz - Own work, CC BY-SA 4.0, https://commons.wikimedia.org/w/index.php?curid=102127221', 'https://en.wikipedia.org/wiki/Rhus_aromatica', -6);
